function main() {

  var sample1 = "bvwbjplbgvbhsrlpgdmjqwftvncz";
  var sample2 = "nppdvjthqldpwncqszvftbrmjlhg";
  var input1 = "";
  var input2 = [];
  var input3 = [];
  var output1 = "";
  var output2 = "";

  var total1 = 0;
  var total2 = 0;

  // part 1

  total1 = markerpos(input1);

  function markerpos(n) {
    var answer = 0;
    for (var i = 0; i < n.length - 4; i++) {
      if (n[i] != n[i + 1] && n[i] != n[i + 2] && n[i] != n[i + 3] && n[i + 1] != n[i + 2] && n[i + 1] != n[i + 3] && n[i + 2] != n[i + 3]) { answer = i + 4; i = n.length; }
    }
    return answer;
  }

  // part 2

  total2 = markerposgeneral(input1, 14);

  function markerposgeneral(n, a) {
    var streak = 0;
    var depth = a;
    var broken = 0;
    for (var i = 0; i < n.length - a; i++) {
      for (var j = 1; j < depth; j++) {
        if (n[i] == n[i + j]) { broken = 1; j = a; }
      }
      if (broken == 0) {
        streak++; depth--;
        if (streak == a - 1) { return i + 2; }
      }
      else { streak = 0; depth = a; broken = 0; }
    }
    return 0;
  }

  output1 = total1;
  output2 = total2;

  document.getElementById("bongo").innerHTML = output1;
  document.getElementById("drum").innerHTML = output2;

}

main();